        <a href="{{url}}">

          <img class="card-image main lazyload" data-sizes="auto" src="{{cdn 'img/loading.svg'}}" data-src="{{getImage image 'productgallery_size' (cdn theme_settings.default_image_product)}}" alt="{{image.alt}}" title="{{image.alt}}">
            {{#if theme_settings.product_hover_images '===' 'active'}}
